function showBootstrapAlert(n,t){$("#alertholder").after('<div class="alert alert-'+n+' alert-dismissable"><button type="button" class="close" data-dismiss="alert" aria-label="Close"><span aria-hidden="true">&times;<\/span><\/button>'+t+"<\/div>")}function closeAlert(n){$.ajax({type:"POST",url:"/Alerts/CloseAlert",data:{id:n},success:function(t){console.log(t);t.success?(showBootstrapAlert("success",t.message||"Successfully closed alert "+n),$("#openalerts").load("/Alerts/RefreshOpenAlerts"),$("#closedalerts").load("/Alerts/RefreshClosedAlerts")):showBootstrapAlert("danger",t.message||"Failed to close alert "+n)}})}function closeAllAlerts(){$.ajax({type:"POST",url:"/Alerts/CloseAllAlerts",data:null,success:function(n){console.log(n);n.success?(showBootstrapAlert("success","Successfully closed all alerts"),$("#openalerts").load("/Alerts/RefreshOpenAlerts"),$("#closedalerts").load("/Alerts/RefreshClosedAlerts")):showBootstrapAlert("danger",n.message||"Failed to close all alerts")}})}